import './../style/main.css';
import './../style/font-awesome.min.css'
import './../style/traduler.css';
import Header from "./Header";
import { useNavigate, useLocation } from "react-router-dom";
import {useState} from "react";

function ThemeView(){
    const location = useLocation();
    const data = { ...location.state}
    console.log("info2 " + JSON.stringify(data))


    const [files, setFiles] = useState([]);
    const [currentSlide, setCurrentSlide] = useState(0);

    const handleFileChange = (e) => {
        const newFiles = [...e.target.files];
        setFiles(newFiles);
        setCurrentSlide(0);
    };


    return(
        <div>
                <Header/>
                <div id="main">
                    <br/><a href="#">뒤로가기 </a>

                        <form method="post" encType="multipart/form-data">
                            <div className="inner" style={{textAlign : "center"}}>
                                <h1><input type="hidden" name="placeName" value="placeName"/>{data.placeName}</h1>
                                <h5><input type="hidden" name="mainCategory" value="mainCategory"/>{data.contentMain}</h5>

                                <input type="hidden" id="file"  onChange={handleFileChange(`data.files[0]`)} multiple/>

                                {/*<span className="image main">
                                   <input type="hidden" name="originImageFile" value="mainImageFile"/>
                                   <div><img id="preview" style={{height:"500px"}}/></div>
                               </span>*/}

                            </div>
                        </form>

                        <div className='flex'>
                            <div className="flexA" id="map" style={{width:"50%", height:"520px"}}></div>

                            <div className="flexB">
                                <div className="flexBa">
                                    <strong>주변 맛집을 추천해주세요</strong>
                                </div>
                                <div>
                                    <div id="reply"></div>
                                </div>
                            </div>
                        </div>
                        <div style={{display: "flex"}}>
                            <label style={{width: "50%"}}>주소 : {data.placeAddr}</label>
                            <div style={{width: "50%"}}>

                            </div>
                        </div>
                </div>
        </div>
    )
}
export default ThemeView;
