import React, {useEffect, useState} from 'react';
import Header from "./Header"; // Import the CSS file for styling
import { useNavigate } from "react-router-dom";
import './../style/MyCalendar.css'
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin, {Draggable} from "@fullcalendar/interaction";

const MyPage = () => {
    const [events, setEvents] = useState([]);

    const [state, setState] = useState({
        externalEvents : JSON.parse(localStorage.getItem("watched"))
    })

    useEffect(() => {
  /*      const storedEvents = localStorage.getItem("watched");
        console.log("storedEvents : " + storedEvents)
        if (storedEvents) {
            setEvents(JSON.parse(storedEvents));
        }
 */       const draggableEl = document.getElementById("external-events");
        new Draggable(draggableEl, {
            itemSelector : ".fc-event",
            eventData : function (eventEl){
                console.log("eventel :: " + eventEl.dataset.id)
                console.log("eventel :: " + JSON.stringify(eventEl.dataset))
                let id = eventEl.getAttribute("placeId")
                let name = eventEl.getAttribute("placeName");
                return {
                    id : id,
                    name : name,
                    create : true
                }
            }
        })
    }, []);

    const handleEventDrop = (info) => {
        const { event, prevEvent } = info;
       // console.log("event : " +event.getAttribute("data-id"));
        console.log("event : " +JSON.stringify(event));

        const newEvents = [...events];
        const index = newEvents.findIndex((e) => e.id === event.id);
        newEvents[index] = {
            ...newEvents[index],
            start: event.start,
            end: event.end,
        };
        setEvents(newEvents);
        localStorage.setItem("watched", JSON.stringify(newEvents));
    };

    return (
        <>
            <Header/>
            <div className='calendar-head' >
                <div>
                    <h2>이벤트 목록</h2>
                    <div id="external-events">
                    {
                        state.externalEvents.map((event) => (
                        <div
                            className="fc-event fc-h-event mb-1 fc-daygrid-event fc-daygrid-block-event p-2"
                            title={event.title}
                            data-id={event.id}
                            data-color={event.color}
                            data-custom={event.custom}
                            key={event.id}
                            style={{
                                backgroundColor: event.color,
                                borderColor: event.color,
                                cursor: "pointer"
                            }}
                        >
                            <div className="fc-event-main">
                                <div>
                                    <strong>{event.placeName}</strong>
                                </div>
                                <br/>
                            </div>
                        </div>
                       ))
                    }
                </div>
                </div>
                <div>
                    <FullCalendar
                        plugins={[dayGridPlugin, interactionPlugin]}
                        initialView="dayGridMonth"
                        events={events}
                        editable={true}
                        droppable={true}
                        eventDrop={handleEventDrop}
                    />
                </div>
            </div>
        </>
    );
};

export default MyPage;
