import React, {useEffect, useState} from 'react';
import Header from "./Header"; // Import the CSS file for styling
import { useNavigate } from "react-router-dom";
import './../style/MyCalendar.css'
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin, {Draggable} from "@fullcalendar/interaction";

const MyPage = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const storedEvents = localStorage.getItem("watched");
        console.log("storedEvents : " + storedEvents)
        if (storedEvents) {
            setEvents(JSON.parse(storedEvents));
        }
        const draggableEl = document.getElementById("external-events");
        new Draggable(draggableEl, {
            itemSelector : ".fc-event",
            eventData : function (eventEl){
                let id = eventEl.getAttribute("placeId")
                let name = eventEl.getAttribute("placeName");
                return {
                    id : id,
                    name : name,
                    create : true
                }
            }
        })
    }, []);

    const handleEventDrop = (info) => {
        console.log("info ? " + info);
        const { event, prevEvent } = info;
        const newEvents = [...events];
        const index = newEvents.findIndex((e) => e.id === event.id);
        newEvents[index] = {
            ...newEvents[index],
            start: event.start,
            end: event.end,
        };
        setEvents(newEvents);
        localStorage.setItem("watched", JSON.stringify(newEvents));
    };

    return (
        <>
            <Header/>
            <div className='calendar-head' >
                <div>
                    <h2>이벤트 목록</h2>
                    <ul>
                        {events.map((event, index) => (
                            <li key={index}
                                draggable
                                onDragStart={(e) => {
                                    e.dataTransfer.setData("text/plain", JSON.stringify(event));
                                }}
                            >{event.placeName}  && {event.placeId}</li>
                        ))}
                    </ul>
                </div>
                <div>
                    <FullCalendar
                        plugins={[dayGridPlugin, interactionPlugin]}
                        initialView="dayGridMonth"
                        events={events}
                        editable={true}
                        droppable={true}
                        eventDrop={handleEventDrop}
                    />
                </div>
            </div>
        </>
    );
};

export default MyPage;
