import React, {useEffect, useState} from 'react';
import Header from "./Header"; // Import the CSS file for styling
import { useNavigate } from "react-router-dom";
import './../style/MyCalendar.css'
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";


const MyPage = () => {
    const navigate = useNavigate();

    const [events, setEvents] = useState([]);

    useEffect( () => {
        const storedEvents = localStorage.getItem("watched");
        if (storedEvents) {
            setEvents(JSON.parse(storedEvents));
        }
    }, []);



    const handleEventDrop = ({ event, oldEvent, delta }) => {
        const newEvents = [...events];
        const index = newEvents.findIndex((e) => e.placeName === event.placeName);
        const newEvent = {
            title: event.event.title,
            date: event.event.startStr,
            placeId: event.event.extendedProps.placeId,
        };
        setEvents(newEvents);
        localStorage.setItem("events", JSON.stringify(newEvents));
    };

    return (
        <>
            <Header/>
            <div className='calendar-head' >
                <div>
                    <h2>이벤트 목록</h2>
                    <ul>
                        {events.map((event, index) => (
                            <li key={index}
                                draggable
                                onDragStart={(e) => {
                                    e.dataTransfer.setData("text/plain", JSON.stringify(event));
                                }}
                            >{event.placeName}</li>
                        ))}
                    </ul>
                </div>
                <div>
                    <h2>달력</h2>
                    <FullCalendar
                        plugins={[dayGridPlugin, interactionPlugin]}
                        initialView="dayGridMonth"
                        events={events}
                        editable={true}
                        droppable={true}
                        eventDrop={handleEventDrop}
                    />
                </div>
            </div>
        </>
    );
};

export default MyPage;
